// src/components/Modal.js
import React from 'react';
import './model.css';

const Model = ({ show, message, onClose }) => {
  if (!show) return null;

  return (
    <div className="model-overlay">
      <div className="model">
        <p>{message}</p>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default Model;
